imageData => {
    $c('create-dialog').show({
        status: "NEW",
        date: moment().format('YYYY-MM-DD'),
        photoUrl: imageData,
        userId: $collab.getLoggedUser().id,
        firstName: $collab.getLoggedUser().firstName,
        lastName: $collab.getLoggedUser().lastName,
        email: $collab.getLoggedUser().email,
        currency: "EUR",
        amount: 0 
    });
}

