components.mapper:
=$d('database').filter((element) => {
    return (($d('container-simple_filter').manufacturer === element.manufacturer || $d('container-simple_filter').manufacturer === "" || $d('container-simple_filter').manufacturer == null)
    && ($d('container-simple_filter').category === element.category || $d('container-simple_filter').category === "" || $d('container-simple_filter').category == null)
    && ($d('container-simple_filter').subcategory === element.subcategory || $d('container-simple_filter').subcategory === "" || $d('container-simple_filter').subcategory == null))
})

components.init:
this.getDataForPage = (data, pageSize, page) => {
    return data
        .filter((item, index) => Math.floor(index / pageSize) === page)
        .map(item => ({name:item.name, gwp_total:item.gwp_total}));
}

this.getManufacturerValues = function () {
    uniqueValues = [];

    $d(this).forEach((element) => {
        uniqueValues.push(element.manufacturer);   
    })
    uniqueValues = ([...new Set(uniqueValues)]).sort();
    uniqueValues.filter((element) => {
        element != ""
    })
    return uniqueValues;
}

