this.filterWithCriterias = (selectedManufacturers, selectedCategories, selectedSubcategories) => {
    selectedManufacturers = selectedManufacturers.filter(item => item != null);
    selectedCategories = selectedCategories.filter(item => item != null);
    selectedSubcategories = selectedSubcategories.filter(item => item != null);
    return $d(this).filter(item => {
        return (selectedCategories.includes(item.category) || selectedCategories.length === 0)
            && (selectedSubcategories.includes(item.subcategory) || selectedSubcategories.length === 0)
            && (selectedManufacturers.includes(item.manufacturer) || selectedManufacturers.length === 0);
    });
}

this.getUniqueFieldValues = function(items, fieldName)
{
    return items.reduce((uniqueFieldValues, item) => {
        if (!uniqueFieldValues.includes(item[fieldName])) {
            uniqueFieldValues.push(item[fieldName]);
        }
        return uniqueFieldValues;
    },[]);
}
